#Functions

def handle_with_number(j)
    i = 0
    j.each do |handle|
        if handle =~ /\d/
            i += 1
        end
    end
    puts "Il y a #{i} handles contenants des nombres"
end

def has_Aude(j)
    i = 0
    j.each do |handle|
        if (handle.downcase).include? "aude"
            i += 1
        end
    end
    puts "Il y a #{i} handles contenants le prénom aude"
end

def start_with_upper(j)
    i = 0
    j.each do |handle|
        if handle[1] == (handle[1]).upcase
            i += 1
        end
    end
    puts "Il y a #{i} handles qui commencent par une majuscule"
end

def has_upper(j)
    i = 0
    j.each do |handle|
        if handle =~ /[A-Z]/
            i += 1
        end
    end
    puts "Il y a #{i} handles qui contiennent des majuscules"
end

def number_underscore(j)
    i = 0
    j.each do |handle|
        i += handle.count('_')
    end
    puts "Il y a #{i} handles qui contiennent '_' "
end

journalists_sort = journalists.sort

def fifty_shortest(j)
    shortest_array = Array.new
    temp_array = j.clone
    50.times do 
        shortest = nil
        min = 100
        temp_array.each do |handle|
            if handle.size < min 
                min = handle.size
                shortest = handle
            end
        end
    shortest_array << shortest
    temp_array.delete(shortest)
    end
    puts "Voici les 50 handles les plus cours : #{shortest_array}"
end

